import './estilo.css'
import { Link } from "react-router-dom"

const textoOpcoes = ['Categorias', 'Minha estante', 'Favoritos']

function OpcoesHeader(){
  return(
    <ul className='opcoes'>
        { textoOpcoes.map( (texto) => (
          <Link to={`/${texto}`}>
            <li className='opcao'><p>{texto}</p></li>
          </Link>
        ))}
    </ul>
  )
}

export default OpcoesHeader
